import java.util.Scanner;
public class desvioEncadeado {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner entrada = new Scanner(System.in);
		
		boolean resp, resp1, resp2; 
		
		resp = true && false; // && = and  (e) 
		System.out.println(resp); // FALSE
		
		resp1 = true || false; // || = or  (ou)
		System.out.println(resp1); // TRUE
		
		resp2 = ! true || false; // ! = no
		System.out.println(resp2); // FALSE
		
// VANTAGENS DE USO DO DESVIO ENCADEADO		
		
		
	}

}
